		popped = arr.inner[arr.inner.length - 1];
		Value[] memory newInner = new Value[](arr.inner.length - 1);
		for (uint256 i = 0; i < newInner.length; i++) {
			newInner[i] = arr.inner[i];
		}
		arr.inner = newInner;
	}
}
